@charset "utf-8"; 
a, a:hover {text-decoration: none !important;}
a:hover, a:hover * {color: #facf02 !important;} 
img {display: block !important;} 
html, body, .fan_box, .full_widget, .full_widget * { margin: 0 !important; padding: 0 !important; background: none !important; border: none !important;color: #808080 !important;  } 
.profileimage, .name_block, .connect_widget_connected_text, .connect_widget_not_connected_text, .connect_widget_user_action { display: none !important; } 
.full_widget { position: relative !important; padding-top: 0px !important;color: #808080 !important;  } 
.connect_top, .connect_action, .connect_widget, .connect_button_slider, .connect_button_container, .connect_widget_like_button { overflow: visible !important; display: block !important; height: 24px !important; } 
.connect_widget_interactive_area, .connect_widget_interactive_area td { height: 30px !important; } 
 .connect_top { position: absolute !important; top: 0 !important; left: 0 !important; width: 100% !important; }
.connect_widget_interactive_area { float: right !important; margin-right: 0px !important; border-collapse: collapse important; border-spacing: 0 !important; } 
.connect_widget_like_button { position: relative !important; line-height: 24px !important; } 
.connect_widget_like_button .tombstone_cross { display:block !important;position:absolute !important;height:24px !important;width:60px !important; background:none !important; } 
.connect_widget_like_button .liketext { display: block !important; float: left; height: 24px !important; line-height: 24px !important; padding: 0 0 0 7px !important; color: #fff !important;font-size:11px !important;font-family:Arial !important;cursor:pointer !important; } 
.total { float:left !important;display: block !important;width:200px !important; margin-bottom: 7px !important; padding-top: 1px !important; font-size: 11px !important; font-family: Arial, Helvetica, sans-serif;color: #808080 !important; ; } 
.fan_box .connections { margin-right: -20px !important; } 
.connections_grid { clear:both !important; }
.grid_item { width: 50px !important; margin: 0px 20px 13px 0 !important; } 
.grid_item a { padding-top:2px !important;display: block !important; color: #333 !important; } 
.grid_item a:hover { color: #f12b63 !important; } 
.grid_item img { width: 50px !important;height:50px !important;margin-bottom:3px !important; } 
.mts { display:none !important; }