function getURLVar(key) {
	var value = [];
	
	var query = String(document.location).split('?');
	
	if (query[1]) {
		var part = query[1].split('&');

		for (i = 0; i < part.length; i++) {
			var data = part[i].split('=');
			
			if (data[0] && data[1]) {
				value[data[0]] = data[1];
			}
		}
		
		if (value[key]) {
			return value[key];
		} else {
			return '';
		}
	}
} 

function addToCart(product_id, quantity) {
	quantity = typeof(quantity) != 'undefined' ? quantity :1;

	$.ajax({
		url:'index.php?route=checkout/cart/add',
		type:'post',
		data:'product_id=' + product_id + '&quantity=' + quantity,
		dataType:'json',
		success:function(json) {			
			if (json['redirect']) {
				location = json['redirect'];
			}
			
			if (json['success']) {

				$.magnificPopup.open({
				  items:{
				    src:'<div class="clearfix">' + json['success'] + '<br><br><a href="#" class="popup-modal-dismiss button-continue-shopping">« ' + continue_shopping_text + '</a><a href="' + checkout_url + '" class="button-checkout">' + checkout_text + ' »</a></div>', 
				    type:'inline'
				  },
				  fixedContentPos:false,
				  fixedBgPos:true,
				  overflowY:'auto',
				  closeBtnInside:true,
				  preloader:false,
				  midClick:true,
				  removalDelay:300,
				  mainClass:'notification'
				});
				
				$('#cart_block').load('index.php?route=module/cart #cart_block > *');
				
			}	
		}
	});
}

$(document).on('click', '.popup-modal-dismiss', function (e) {
	e.preventDefault();
	$.magnificPopup.close();
});

